def run(average_range, sleep):
    while True:
        get_temp(average_range, sleep)

def get_temp(average_range, sleep):
    value = apin()
    for i in range(average_range):
        time.sleep_ms(sleep)
        value += apin()
    avg = value // (average_range + 1)
    if avg > 1000:
        num = avg // 10
        num = num - 100 + 1000
        temp10 = TABLE[num]
    else:
        temp10 = TABLE[avg]
    temp = temp10 // 10
    temp_decimal = temp10 % 10
    print("{}: {}.{} C".format(avg, temp, temp_decimal))
    return temp, temp_decimal
